from typing import List, TypeVar

T = TypeVar("T")


class Stack(List[T]):
    """A small shim over builtin list."""

    @property
    @timeout_decorator.timeout(LOCAL_TIMEOUT)
    def top(self) -> T:
        """Get top of stack."""
        return self[-1]

    @timeout_decorator.timeout(LOCAL_TIMEOUT)
    def push(self, item: T) -> None:
        """Push an item on to the stack (append in stack nomenclature)."""
        self.append(item)
